import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const [open, setOpen] = useState(false);

    return (
      <nav className="bg-red-800 text-white">
        <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
          <div className="text-xl font-bold">PracGes</div>

          <div className="md:hidden">
            <button onClick={() => setOpen(!open)}>
              ☰
            </button>
          </div>

          <div className="hidden md:flex gap-4">
            <Link to="/">Inicio</Link>
            <Link to="/normativa">Normativa</Link>
            <Link to="/login">Login</Link>
          </div>
        </div>

        {/* Menú móvil */}
        {open && (
  <>
    {/* Fondo oscuro */}
    <div
      className="fixed inset-0 bg-blend-color bg-opacity-30 z-40"
      onClick={() => setOpen(false)}
    ></div>

    {/* Menú lateral */}
    <div className="fixed top-0 left-0 h-full w-1/2 bg-red-800 text-white p-6 z-50 shadow-lg">
      <button
        className="mb-4 text-xl"
        onClick={() => setOpen(false)}
      >
        ✕
      </button>
      <nav className="flex flex-col gap-4">
        <Link to="/" onClick={() => setOpen(false)}>Inicio</Link>
        <Link to="/normativa" onClick={() => setOpen(false)}>Normativa</Link>
        <Link to="/login" onClick={() => setOpen(false)}>Login</Link>
      </nav>
    </div>
  </>
)}
      </nav>
    );
  };

  export default Navbar;
