const AlumnoDashboard = () => {
  const navigate = useNavigate();
  const [userData, setUserData] = useState({
    dni: '',
    nombre: '',
    apellidos: '',
    telefono: ''
  });
  const [loading, setLoading] = useState(true); // Estado de carga

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (!token) {
      navigate('/login');
      return;
    }

    fetch('http://localhost:5000/api/alumno/me', {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    })
      .then(res => {
        if (!res.ok) {
          // Token inválido o no autorizado
          localStorage.removeItem('token');
          localStorage.removeItem('rol');
          throw new Error('No autorizado');
        }
        return res.json();
      })
      .then(data => {
        // Ajusta según cómo tu backend devuelva los datos
        const usuario = data.user || data;

        setUserData({
          dni: usuario.dni || '',
          nombre: usuario.nombre || '',
          apellidos: usuario.apellidos || '',
          telefono: usuario.telefono || ''
        });
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        navigate('/login'); // Redirige al login solo si hay error
      });
  }, [navigate]);

  if (loading) return <p className="loading-text">Cargando datos del alumno...</p>;

  return (
    <div className="page-layout">
      <Navbar />
      <h2 className="page-title">Panel del Alumno</h2>
      <main className="dashboard-main">
        <div className="dashboard-card">
          <h1 className="dashboard-heading">Bienvenido Alumno</h1>
          <div className="dashboard-text">
            <p>Usuario conectado con DNI: <strong>{userData.dni}</strong></p> 
            <p>Nombre: <strong>{userData.nombre} {userData.apellidos}</strong></p> 
            <p>Teléfono: <strong>{userData.telefono}</strong></p> 
            <strong>¡Gracias por ser parte de nuestra comunidad!</strong>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default AlumnoDashboard;
