import React from 'react';
import Navbar from './Navbar';
import Footer from './Footer';

const PlanFormativoVisualizar = () => {
  // Aquí podrías usar fetch para obtener el plan formativo del alumno
  const planEjemplo = [
    { modulo: 'Módulo 1', descripcion: 'Instalación y mantenimiento de sistemas' },
    { modulo: 'Módulo 2', descripcion: 'Redes y seguridad' },
  ];

  return (
    <div className="flex flex-col min-h-screen">
      <Navbar />
      <main className="flex-1 p-6 bg-gray-50">
        <h1 className="text-3xl font-bold text-red-800 mb-4 text-center">Plan Formativo</h1>
        <div className="max-w-4xl mx-auto space-y-4">
          {planEjemplo.map((item, index) => (
            <div key={index} className="border p-4 rounded shadow bg-white">
              <h2 className="text-xl font-semibold">{item.modulo}</h2>
              <p>{item.descripcion}</p>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default PlanFormativoVisualizar;
